search.query:
`cs_wineventlog_security` TERM(EventCode=4674) host IN ($tkn_host$) | rex "(?s)Desired Access:\s?(?<desired_accesses>.+?)[\s
]*Privileges:" | makemv tokenizer="[\s*]?([^
]+)
?" desired_accesses | eval desired_accesses = mvmap(desired_accesses, trim(desired_accesses)) | fillnull value="-" object_file_path | stats values(host) as hosts, values(desired_accesses) as desired_accesses, values(privilege_id) as privileges, values(process) as processes, count by user, object_file_path, Object_Handle, Object_Server, status | eval status=status." (".count.")" | stats values(status) as status, values(hosts) as hosts, values(desired_accesses) as desired_accesses, values(privileges) as privileges, values(processes) as processes, sum(count) as count by user, object_file_path, Object_Handle, Object_Server | sort - count | fields - count | lookup windows_privilege_lookup privilege_id AS privileges OUTPUT privilege as privilege_new | eval privileges=if(isnotnull(privilege_new),privileges." (".privilege_new.")" ,privileges) | fields - privilege_new
